import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";

export default function CustomerDashboard() {
  const { isAuthenticated, role, userId } = useSelector((state) => state.auth);

  // Profile state
  const [profile, setProfile] = useState({
    customerId: "", // Dynamically set from the API
    userName: "",
    contact: "",
    email: "",
    address: "",
    password: "",
  });
  const [loading, setLoading] = useState(false);
  const [isEditing, setIsEditing] = useState(false);

  // Handle form changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setProfile({ ...profile, [name]: value });
  };

  // Handle the update profile API call
  const handleUpdateProfile = () => {
    setLoading(true);
    fetch(`/update/customer/${profile.customerId}`, 
 {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(profile),
    })
      .then((response) => response.json())
      .then((data) => {
        setProfile(data);
        setIsEditing(false);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error updating profile:", error);
        setLoading(false);
      });
  };

  // Fetch the profile data on initial load
  useEffect(() => {
    if (isAuthenticated && userId) {
      fetch(`http://localhost:9000/custbyid/${profile.customerId}`)
        .then((response) => response.json())
        .then((data) => {
          setProfile(data);
        })
        .catch((error) => console.error("Error fetching profile:", error));
    }
  }, [isAuthenticated, userId]); // Dependencies to refetch if either isAuthenticated or userId changes

  return (
    <div>
      <h2>Customer Dashboard</h2>
      {role?.roleName === "Customer" && isAuthenticated ? (
        <div>
          <h3>Profile</h3>
          {isEditing ? (
            <div>
              <label>
                User Name:
                <input
                  type="text"
                  name="userName"
                  value={profile.userName}
                  onChange={handleChange}
                />
              </label>
              <label>
                Email:
                <input
                  type="email"
                  name="email"
                  value={profile.email}
                  onChange={handleChange}
                />
              </label>
              <label>
                Contact:
                <input
                  type="text"
                  name="contact"
                  value={profile.contact}
                  onChange={handleChange}
                />
              </label>
              <label>
                Address:
                <input
                  type="text"
                  name="address"
                  value={profile.address}
                  onChange={handleChange}
                />
              </label>
              <div>
                <button onClick={handleUpdateProfile} disabled={loading}>
                  {loading ? "Updating..." : "Update Profile"}
                </button>
                <button onClick={() => setIsEditing(false)}>Cancel</button>
              </div>
            </div>
          ) : (
            <div>
              <p>
                <strong>Customer ID:</strong> {profile.customerId}
              </p>
              <p>
                <strong>User Name:</strong> {profile.userName}
              </p>
              <p>
                <strong>Email:</strong> {profile.email}
              </p>
              <p>
                <strong>Contact:</strong> {profile.contact}
              </p>
              <p>
                <strong>Address:</strong> {profile.address}
              </p>
              <button onClick={() => setIsEditing(true)}>Edit Profile</button>
            </div>
          )}
        </div>
      ) : (
        <p>Please log in as a customer.</p>
      )}
    </div>
  );
}
