import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { login } from '../../redux/slices/authSlice';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const validateInput = () => {
    if (!email || !password) {
      setErrorMessage('Both email and password are required');
      return false;
    }
    return true;
  };

  const handleLogin = async (e) => {
    e.preventDefault();

    // Validate inputs
    if (!validateInput()) return;

    try {
      const response = await axios.post('http://localhost:8080/login', { email, password });

      const { user, role } = response.data;
      dispatch(login({ user, role }));

      if (role.roleName === 'Admin') navigate('/dashboard/admin');
      else if (role.roleName === 'Customer') navigate('/dashboard/customer');
      else if (role.roleName === 'Owner') navigate('/dashboard/owner');
    } catch (error) {
      if (error.response) {
        // Handle server errors
        if (error.response.status === 401) {
          setErrorMessage('Invalid email or password');
        } else {
          setErrorMessage('An error occurred, please try again');
        }
      } else {
        // Handle network errors or unexpected issues
        setErrorMessage('Network error, please check your connection');
      }
    }
  };

  return (
    <div className="container mt-5">
      <h2>Login</h2>
      {errorMessage && <div className="alert alert-danger">{errorMessage}</div>} {/* Display error message */}
      <form onSubmit={handleLogin}>
        <div className="mb-3">
          <label htmlFor="email" className="form-label">Email</label>
          <input
            type="email"
            className="form-control"
            id="email"
            placeholder="Your Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="password" className="form-label">Password</label>
          <input
            type="password"
            className="form-control"
            id="password"
            placeholder="Your Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit" className="btn btn-primary">Login</button>
      </form>
    </div>
  );
};

export default Login;
