import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { useForm } from "react-hook-form";
import { useSelector, useDispatch } from "react-redux";

const OwnerDashboard = () => {
  const dispatch = useDispatch();

  // Use Redux state for user and agency details
  const agencyDetails = useSelector((state) => state.agency.details);
  const agencyId = useSelector((state) => state.user.id);

  const [cities, setCities] = useState([]);
  const [successMessage, setSuccessMessage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [showForm, setShowForm] = useState(false);

  const { register, handleSubmit, setValue, formState: { errors } } = useForm();

  useEffect(() => {
    if (agencyDetails) {
      setValue("userName", agencyDetails.user?.userName || "");
      setValue("contact", agencyDetails.contact || "");
      setValue("address", agencyDetails.address || "");
      setValue("gstNo", agencyDetails.gstNo || "");
      setValue("cityId", agencyDetails.city?.cityId || "");
    }
  }, [agencyDetails, setValue]);

  useEffect(() => {
    // Fetch cities for the dropdown
    const fetchCities = async () => {
      try {
        const response = await axios.get("http://localhost:8080/cities");
        setCities(response.data);
      } catch (error) {
        console.error("Error fetching cities", error);
      }
    };

    fetchCities();
    // Dispatch action to fetch agency details
    if (agencyId) {
      dispatch(fetchAgencyDetails(agencyId)); // Replace with your Redux action
    }
  }, [agencyId, dispatch]);

  const handleUpdate = async (data) => {
    const updatedData = {
      ...agencyDetails,
      user: { ...agencyDetails.user, userName: data.userName },
      contact: data.contact,
      address: data.address,
      gstNo: data.gstNo,
      city: cities.find((city) => city.cityId === parseInt(data.cityId)),
    };

    try {
      const response = await axios.put(
        `http://localhost:8080/api/car-rental-agencies/${agencyId}`,
        updatedData,
        { headers: { "Content-Type": "application/json" } }
      );
      setSuccessMessage("Details updated successfully!");
      setErrorMessage("");
      dispatch(updateAgencyDetails(response.data)); // Update Redux state
    } catch (error) {
      console.error("Error updating agency details", error);
      setErrorMessage("Failed to update details.");
    }
  };

  return (
    <div>
      <nav className="navbar navbar-expand-lg navbar-light bg-light shadow-sm">
        <div className="container">
          <Link className="navbar-brand" to="/">CarRental</Link>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNav"
            aria-controls="navbarNav"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav ms-auto">
              <li className="nav-item">
                <Link className="nav-link" to="/owner-home">Home</Link>
              </li>
              <li className="nav-item">
                <button
                  className="btn btn-link nav-link"
                  onClick={() => setShowForm(true)}
                >
                  Update Profile
                </button>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="logout">Logout</Link>
              </li>
            </ul>
          </div>
        </div>
      </nav>

      <div className="container mt-5">
        {showForm && (
          <div className="row justify-content-center">
            <div className="col-lg-8">
              <h2 className="text-center mb-4">Update Profile</h2>
              <form
                onSubmit={handleSubmit(handleUpdate)}
                className="p-4 shadow rounded bg-white"
              >
                <div className="mb-3">
                  <label htmlFor="userName" className="form-label">Name</label>
                  <input
                    type="text"
                    className="form-control"
                    id="userName"
                    {...register("userName", { required: "Name is required", minLength: { value: 5, message: "Name must have at least 5 characters" } })}
                  />
                  {errors.userName && <p className="text-danger">{errors.userName.message}</p>}
                </div>
                {/* Other fields */}
              </form>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default OwnerDashboard;
