import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  loggedIn: false,
  user: null,
};

const authSlice = createSlice({
  name: 'auth',
  initialState,
  reducers: {
    login: (state, action) => {
      return {
        loggedIn: true,
        user: action.payload,
      };
    },
    logout: (state) => {
      return {
        loggedIn: false,
        user: null,
      };
    },
    loginFailed: (state, action) => {
      return {
        ...state, 
        error: action.payload 
      }; 
    },
    registerFailed: (state, action) => {
      return {
        ...state, 
        error: action.payload 
      }; 
    },
  },
});

export const { login, logout, loginFailed, registerFailed } = authSlice.actions;
export default authSlice.reducer;
