import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { login, loginFailed } from '../features/auth/authSlice';
import authService from '../services/authService'; // Import the authService

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null); // To display errors
  const dispatch = useDispatch();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await authService.login({ username, password }); // Call the API
      if (response) {
        dispatch(login(response)); // Dispatch login with user data
        setError(null); // Clear any existing errors
        alert('Login successful!'); // Optional: Notify user
      }
    } catch (error) {
      dispatch(loginFailed(error.message));
      setError('Login failed. Please check your credentials.'); // Set error message
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="username">Username:</label>
        <input
          type="text"
          id="username"
          name="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <br />
        <label htmlFor="password">Password:</label>
        <input
          type="password"
          id="password"
          name="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <br />
        <button type="submit">Login</button>
      </form>
      {error && <p style={{ color: 'red' }}>{error}</p>} {/* Display error if any */}
    </div>
  );
};

export default Login;
