import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  loggedIn: false,
  user: null,
  error: null
};

const authSlice = createSlice({
  name: 'auth',
  initialState,
  reducers: {
    login: (state, action) => {
      state.loggedIn = true;
      state.user = action.payload;
    },
    logout: (state) => {
      state.loggedIn = false;
      state.user = null;
    },
    loginFailed: (state, action) => {
      state.error = action.payload;
    },
    register: (state, action) => {
      state.loggedIn = true;
      state.user = action.payload;
    },
    registerFailed: (state, action) => {
      state.error = action.payload;
    }
  }
});

export const { login, logout, loginFailed, register, registerFailed } = authSlice.actions;
export default authSlice.reducer;
