import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { login } from "../../redux/slices/authSlice";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const validateInput = () => {
    if (!email || !password) {
      setErrorMessage("Both email and password are required");
      return false;
    }
    return true;
  };

  const handleLogin = async (e) => {
    e.preventDefault();

    // Validate inputs
    if (!validateInput()) return;

    try {
      const response = await axios.post("http://localhost:8080/login", {
        email,
        password,
      });

      // Log the response to check what data is coming from the server
      console.log(response.data); // Ensure { user, role } is returned

      const { user, role } = response.data;

      // Check if user and role are available in response
      if (user && role) {
        // Dispatch the login action with user and role
        dispatch(login({ user, role }));

        // Log the Redux state after dispatch
        console.log("Logged in, state updated:", { user, role });

        // Redirect based on role
        if (role.roleName === "Admin") navigate("/dashboard/admin");
        else if (role.roleName === "Customer") navigate("/dashboard/customer");
        else if (role.roleName === "Owner") navigate("/dashboard/owner");
      } else {
        setErrorMessage("Unexpected response format from server");
      }
    } catch (error) {
      if (error.response) {
        // Handle server errors based on status codes and response messages
        if (error.response.status === 401) {
          const errorData = error.response.data;
          if (errorData.message === "Invalid password") {
            setErrorMessage("Incorrect password");
          } else if (errorData.message === "User not found") {
            setErrorMessage("Username not found");
          } else {
            setErrorMessage("Invalid email or password");
          }
        } else {
          setErrorMessage("An error occurred, please try again");
        }
      } else {
        // Handle network errors or unexpected issues
        setErrorMessage("Network error, please check your connection");
      }
    }
  };

  return (
    <div className="container mt-5">
      <h2>Login</h2>
      {errorMessage && (
        <div className="alert alert-danger">{errorMessage}</div>
      )}{" "}
      {/* Display error message */}
      <form onSubmit={handleLogin}>
        <div className="mb-3">
          <label htmlFor="email" className="form-label">
            Email
          </label>
          <input
            type="email"
            className="form-control"
            id="email"
            placeholder="Your Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="password" className="form-label">
            Password
          </label>
          <input
            type="password"
            className="form-control"
            id="password"
            placeholder="Your Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Login
        </button>
      </form>
    </div>
  );
};

export default Login;
