import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { useForm } from "react-hook-form";
import { useSelector } from "react-redux";

const OwnerDashboard = () => {
  const user = useSelector((state) => state.auth.user); // Fetch user state from Redux
  const [formData, setFormData] = useState({
    user: {
      userName: "",
      email: "",
      password: "",
      role: { roleId: 3, roleName: "Agency" },
    },
    city: { cityId: "", cityName: "" },
    address: "",
    contact: "",
    gstNo: "",
  });

  const [originalData, setOriginalData] = useState({});
  const [cities, setCities] = useState([]);
  const [successMessage, setSuccessMessage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [showForm, setShowForm] = useState(false); // State to toggle form visibility

  const {
    register,
    handleSubmit,
    formState: { errors },
    setValue,
  } = useForm({ mode: "onChange" });

  useEffect(() => {
    if (!user) {
      setErrorMessage("User not logged in or missing details.");
      return;
    }

    const fetchAgencyDetails = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8080/api/car-rental-agencies/${user.ownerId}` // Use ownerId from Redux
        );
        setFormData(response.data);
        setOriginalData(response.data);

        // Set form values for react-hook-form managed fields
        setValue("userName", response.data.user.userName);
        setValue("contact", response.data.contact);
        setValue("address", response.data.address);
        setValue("gstNo", response.data.gstNo);
        setValue("cityId", response.data.city.cityId);
      } catch (error) {
        console.error("Error fetching agency details", error);
        setErrorMessage("Failed to fetch agency details.");
      }
    };

    const fetchCities = async () => {
      try {
        const response = await axios.get("http://localhost:8080/cities");
        setCities(response.data);
      } catch (error) {
        console.error("Error fetching cities", error);
      }
    };

    fetchAgencyDetails();
    fetchCities();
  }, [user, setValue]);

  const handleUpdate = async (data) => {
    const updatedData = { ...formData };
    updatedData.user = {
      ...updatedData.user,
      userName: data.userName,
      password: updatedData.user.password, // Keep the password unchanged
    };
    updatedData.contact = data.contact;
    updatedData.address = data.address;
    updatedData.gstNo = data.gstNo;
    updatedData.city = cities.find(
      (city) => city.cityId === parseInt(data.cityId)
    );

    try {
      const response = await axios.put(
        `http://localhost:8080/api/car-rental-agencies/${user.ownerId}`, // Use ownerId from Redux
        updatedData,
        {
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      setSuccessMessage("Details updated successfully!");
      setOriginalData(response.data);
      setErrorMessage("");
    } catch (error) {
      console.error("Error updating agency details", error);
      setErrorMessage(
        error.response?.data?.message || "Failed to update details."
      );
    }
  };

  return (
    <div>
      <nav className="navbar navbar-expand-lg navbar-light bg-light shadow-sm">
        <div className="container">
          <Link className="navbar-brand" to="/">
            CarRental
          </Link>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNav"
            aria-controls="navbarNav"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav ms-auto">
              <li className="nav-item">
                <Link className="nav-link" to="/owner-home">
                  Home
                </Link>
              </li>
              <li className="nav-item">
                <button
                  className="btn btn-link nav-link"
                  onClick={() => setShowForm(true)}
                >
                  Update Profile
                </button>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="logout">
                  Logout
                </Link>
              </li>
            </ul>
          </div>
        </div>
      </nav>

      <div className="container mt-5">
        {showForm && (
          <div className="row justify-content-center">
            <div className="col-lg-8">
              <h2 className="text-center mb-4">Update Profile</h2>
              <form
                onSubmit={handleSubmit(handleUpdate)}
                className="p-4 shadow rounded bg-white"
              >
                {/* Fields (same as before) */}

                {successMessage && (
                  <div className="alert alert-success mt-3" role="alert">
                    {successMessage}
                  </div>
                )}
                {errorMessage && (
                  <div className="alert alert-danger mt-3" role="alert">
                    {errorMessage}
                  </div>
                )}
              </form>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default OwnerDashboard;
