import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  user: null,
  isLoggedIn: false,
  error: null,
};

const authSlice = createSlice({
  name: 'auth',
  initialState,
  reducers: {
    login: (state, action) => {
      state.user = action.payload; 
      state.isLoggedIn = true;
      state.error = null;
    },
    logout: (state) => {
      state.user = null;
      state.isLoggedIn = false;
      state.error = null;
    },
    register: (state, action) => { 
      state.user = action.payload; 
      state.isLoggedIn = true; 
      state.error = null;
    },
    loginFailed: (state, action) => {
      state.user = null;
      state.isLoggedIn = false;
      state.error = action.payload;
    },
    registerFailed: (state, action) => { 
      state.user = null;
      state.isLoggedIn = false;
      state.error = action.payload; 
    }, 
  },
});

export const { login, logout, register, loginFailed, registerFailed } = authSlice.actions;
export default authSlice.reducer;
