import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { login } from '../../redux/slices/authSlice';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    if (!validateInput()) return;

    try {
      const response = await axios.post("http://localhost:8080/login", {
        email,
        password,
      });

      // Log the response to check the structure
      console.log("Login response:", response.data);

      const { user, role } = response.data; // Ensure these fields exist in the response

      if (user && role) {
        dispatch(login({ user, role }));
        console.log("User and role set in Redux:", { user, role });

        if (role.roleName === "Admin") navigate("/dashboard/admin");
        else if (role.roleName === "Customer") navigate("/dashboard/customer");
        else if (role.roleName === "Owner") navigate("/dashboard/owner");
      } else {
        setErrorMessage("Unexpected response format from server");
      }
    } catch (error) {
      setErrorMessage("An error occurred, please try again");
    }
  };


  return (
    <div className="container mt-5">
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <div className="mb-3">
          <label htmlFor="email" className="form-label">Email</label>
          <input
            type="email"
            className="form-control"
            id="email"
            placeholder="Your Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="password" className="form-label">Password</label>
          <input
            type="password"
            className="form-control"
            id="password"
            placeholder="Your Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit" className="btn btn-primary">Login</button>
      </form>
    </div>
  );
};

export default Login;
