import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { getAgencyDetails, getCities, updateAgencyDetails } from "./api";
import "./styles.css";

const OwnerDashboard = () => {
  const [formData, setFormData] = useState({
    user: { userName: "", email: "", password: "", role: { roleId: 3, roleName: "Agency" } },
    city: { cityId: "", cityName: "" },
    address: "",
    contact: "",
    gstNo: "",
  });
  const [originalData, setOriginalData] = useState({});
  const [cities, setCities] = useState([]);
  const [agencyId, setAgencyId] = useState(1); // Adjust to fetch dynamically
  const [successMessage, setSuccessMessage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  useEffect(() => {
    const fetchDetails = async () => {
      try {
        const agencyData = await getAgencyDetails(agencyId);
        setFormData(agencyData);
        setOriginalData(agencyData);

        const citiesData = await getCities();
        setCities(citiesData);
      } catch (error) {
        console.error("Error fetching data", error);
        setErrorMessage("Failed to fetch agency details.");
      }
    };
    fetchDetails();
  }, [agencyId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.includes("user.")) {
      const field = name.split(".")[1];
      setFormData({
        ...formData,
        user: { ...formData.user, [field]: value },
      });
    } else if (name === "cityId") {
      const selectedCity = cities.find((city) => city.cityId === parseInt(value));
      setFormData({
        ...formData,
        city: { cityId: selectedCity.cityId, cityName: selectedCity.cityName },
      });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    const updatedData = {};
    Object.keys(formData).forEach((key) => {
      if (JSON.stringify(formData[key]) !== JSON.stringify(originalData[key])) {
        updatedData[key] = formData[key];
      }
    });

    try {
      const updatedAgency = await updateAgencyDetails(agencyId, updatedData);
      setOriginalData(updatedAgency);
      setSuccessMessage("Details updated successfully!");
      setErrorMessage("");
    } catch (error) {
      console.error("Error updating details", error);
      setErrorMessage("Failed to update details.");
    }
  };

  return (
    <div>
      <nav className="navbar navbar-expand-lg navbar-light bg-light shadow-sm">
        <div className="container">
          <Link className="navbar-brand" to="/">CarRental</Link>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav ms-auto">
              <li className="nav-item"><Link className="nav-link" to="logout">Logout</Link></li>
            </ul>
          </div>
        </div>
      </nav>
      <div className="container mt-5">
        <div className="row justify-content-center">
          <div className="col-lg-8">
            <h2>Owner Dashboard</h2>
            <form onSubmit={handleUpdate} className="p-4 shadow rounded bg-white">
              <div>
                <label htmlFor="userName">Name</label>
                <input type="text" id="userName" name="user.userName" value={formData.user.userName} onChange={handleChange} className="form-control" />
              </div>
              <div>
                <label htmlFor="email">Email</label>
                <input type="email" id="email" name="user.email" value={formData.user.email} disabled className="form-control" />
              </div>
              <div>
                <label htmlFor="password">Password</label>
                <input type="password" id="password" name="user.password" value={formData.user.password} onChange={handleChange} className="form-control" />
              </div>
              <div>
                <label htmlFor="cityId">City</label>
                <select id="cityId" name="cityId" value={formData.city.cityId} onChange={handleChange} className="form-control">
                  <option value="">Select City</option>
                  {cities.map((city) => (
                    <option key={city.cityId} value={city.cityId}>{city.cityName}</option>
                  ))}
                </select>
              </div>
              <div>
                <label htmlFor="contact">Contact</label>
                <input type="text" id="contact" name="contact" value={formData.contact} onChange={handleChange} className="form-control" />
              </div>
              <div>
                <label htmlFor="address">Address</label>
                <input type="text" id="address" name="address" value={formData.address} onChange={handleChange} className="form-control" />
              </div>
              <div>
                <label htmlFor="gstNo">GST Number</label>
                <input type="text" id="gstNo" name="gstNo" value={formData.gstNo} onChange={handleChange} className="form-control" />
              </div>
              <button type="submit" className="btn btn-primary">Update Details</button>
              {successMessage && <div className="alert alert-success">{successMessage}</div>}
              {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default OwnerDashboard;
