import React, { useState, useEffect } from "react";
import axios from "axios";
import { useForm } from "react-hook-form";

const OwnerDashboard = () => {
  const [formData, setFormData] = useState({
    user: {
      userName: "",
      email: "",
      password: "",
      role: { roleId: 3, roleName: "Agency" },
    },
    city: { cityId: "", cityName: "" },
    address: "",
    contact: "",
    gstNo: "",
  });

  const [cities, setCities] = useState([]);
  const [agencyId, setAgencyId] = useState(1); // Adjust to fetch agency ID dynamically
  const [successMessage, setSuccessMessage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const {
    register,
    handleSubmit,
    setValue,
    reset,
    formState: { errors },
  } = useForm();

  useEffect(() => {
    // Fetch agency details by ID
    const fetchAgencyDetails = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8080/api/car-rental-agencies/${agencyId}`
        );
        const data = response.data;
        setFormData(data); // Update local state
        reset(data); // Populate react-hook-form fields
      } catch (error) {
        console.error("Error fetching agency details", error);
        setErrorMessage("Failed to fetch agency details.");
      }
    };

    // Fetch list of cities
    const fetchCities = async () => {
      try {
        const response = await axios.get("http://localhost:8080/cities");
        console.log("Cities response:", response.data); // Debugging response
        setCities(response.data);
      } catch (error) {
        console.error("Error fetching cities:", error);
        setErrorMessage("Failed to fetch cities.");
      }
    };

    fetchAgencyDetails();
    fetchCities();
  }, [agencyId, reset]);

  const onSubmit = async (data) => {
    try {
      const response = await axios.put(
        `http://localhost:8080/api/car-rental-agencies/${agencyId}`,
        data,
        {
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      setSuccessMessage("Details updated successfully!");
      setFormData(response.data); // Update formData with the latest data
      setErrorMessage("");
    } catch (error) {
      console.error("Error updating agency details", error);

      // Handle backend error message
      if (error.response && error.response.data) {
        setErrorMessage(
          `Update failed: ${error.response.data.message || "Unknown error"}`
        );
      } else {
        setErrorMessage("Failed to update details. Please try again.");
      }
    }
  };

  return (
    <div className="container mt-5 d-flex justify-content-center">
      <div className="col-md-6">
        <h2 className="text-center mb-4">Owner Dashboard</h2>
        <form
          onSubmit={handleSubmit(onSubmit)}
          className="p-4 shadow-lg rounded bg-light"
        >
          <div className="mb-3">
            <label htmlFor="userName" className="form-label">
              Name
            </label>
            <input
              type="text"
              className="form-control"
              id="userName"
              {...register("user.userName", {
                required: "Name is required",
                minLength: {
                  value: 2,
                  message: "Name must have at least 2 characters",
                },
              })}
              defaultValue={formData.user.userName}
            />
            {errors.user?.userName && (
              <p className="text-danger">{errors.user.userName.message}</p>
            )}
          </div>

          <div className="mb-3">
            <label htmlFor="email" className="form-label">
              Email
            </label>
            <input
              type="email"
              className="form-control"
              id="email"
              {...register("user.email")}
              defaultValue={formData.user.email}
              disabled
            />
          </div>

          <div className="mb-3">
            <label htmlFor="cityId" className="form-label">
              City
            </label>
            <select
              className="form-control"
              id="cityId"
              {...register("city.cityId", {
                required: "City selection is required",
              })}
              defaultValue={formData.city.cityId}
            >
              <option value="">Select City</option>
              {cities.map((city) => (
                <option key={city.cityId} value={city.cityId}>
                  {city.cityName}
                </option>
              ))}
            </select>
            {errors.city?.cityId && (
              <p className="text-danger">{errors.city.cityId.message}</p>
            )}
          </div>

          <div className="mb-3">
            <label htmlFor="contact" className="form-label">
              Contact
            </label>
            <input
              type="text"
              className="form-control"
              id="contact"
              {...register("contact", {
                required: "Contact number is required",
                pattern: {
                  value: /^\d{10}$/,
                  message: "Contact number must be 10 digits",
                },
              })}
              defaultValue={formData.contact}
            />
            {errors.contact && (
              <p className="text-danger">{errors.contact.message}</p>
            )}
          </div>

          <div className="mb-3">
            <label htmlFor="address" className="form-label">
              Address
            </label>
            <input
              type="text"
              className="form-control"
              id="address"
              {...register("address", { required: "Address is required" })}
              defaultValue={formData.address}
            />
            {errors.address && (
              <p className="text-danger">{errors.address.message}</p>
            )}
          </div>

          <div className="mb-3">
            <label htmlFor="gstNo" className="form-label">
              GST Number
            </label>
            <input
              type="text"
              className="form-control"
              id="gstNo"
              {...register("gstNo", {
                required: "GST number is required",
                pattern: {
                  value: /^[0-9]{15}$/,
                  message: "GST number must be 15 digits",
                },
              })}
              defaultValue={formData.gstNo}
            />
            {errors.gstNo && (
              <p className="text-danger">{errors.gstNo.message}</p>
            )}
          </div>

          <button
            type="submit"
            className="btn btn-primary w-100"
            style={{ transition: "background-color 0.5s ease" }}
          >
            Update Details
          </button>

          {/* Success and error messages */}
          {successMessage && (
            <div className="alert alert-success mt-3" role="alert">
              {successMessage}
            </div>
          )}
          {errorMessage && (
            <div className="alert alert-danger mt-3" role="alert">
              {errorMessage}
            </div>
          )}
        </form>
      </div>
    </div>
  );
};

export default OwnerDashboard;
