import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { login } from "../../redux/slices/authSlice";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const validateInput = () => {
    if (!email || !password) {
      setErrorMessage("Both email and password are required");
      return false;
    }
    return true;
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8080/login', { email, password });
      const { user } = response.data; // Assuming you're returning user data on successful login
      dispatch(login({ user }));

      // Navigate based on the role
      if (user.role === 'Admin') navigate('/dashboard/admin');
      else if (user.role === 'Customer') navigate('/dashboard/customer');
      else if (user.role === 'Owner') navigate('/dashboard/owner');
    } catch (error) {
      // Handle error, either invalid email/password or network issues
      if (error.response) {
        alert(error.response.data); // Display error message from the backend (e.g., "Invalid email or password")
      } else {
        console.error('Login failed:', error);
      }
    }
  };

  return (
    <div className="container mt-5">
      <h2>Login</h2>
      {errorMessage && (
        <div className="alert alert-danger">{errorMessage}</div>
      )}{" "}
      {/* Display error message */}
      <form onSubmit={handleLogin}>
        <div className="mb-3">
          <label htmlFor="email" className="form-label">
            Email
          </label>
          <input
            type="email"
            className="form-control"
            id="email"
            placeholder="Your Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="password" className="form-label">
            Password
          </label>
          <input
            type="password"
            className="form-control"
            id="password"
            placeholder="Your Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Login
        </button>
      </form>
    </div>
  );
};

export default Login;
