import React, { useState, useEffect } from "react";
import axios from "axios";
import { useForm } from "react-hook-form";

const OwnerDashboard = () => {
  const [cities, setCities] = useState([]);
  const [successMessage, setSuccessMessage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const agencyId = 1; // Adjust to fetch agency ID dynamically

  const {
    register,
    handleSubmit,
    setValue,
    formState: { errors },
  } = useForm({ mode: "onChange" });

  useEffect(() => {
    const fetchAgencyDetails = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8080/api/car-rental-agencies/${agencyId}`
        );
        const agency = response.data;

        // Populate form fields with fetched data
        setValue("userName", agency.user.userName);
        setValue("email", agency.user.email);
        setValue("cityId", agency.city.cityId);
        setValue("contact", agency.contact);
        setValue("address", agency.address);
        setValue("gstNo", agency.gstNo);
      } catch (error) {
        console.error("Error fetching agency details", error);
        setErrorMessage("Failed to fetch agency details.");
      }
    };

    const fetchCities = async () => {
      try {
        const response = await axios.get("http://localhost:8080/cities");
        setCities(response.data);
      } catch (error) {
        console.error("Error fetching cities", error);
      }
    };

    fetchAgencyDetails();
    fetchCities();
  }, [agencyId, setValue]);

  const onSubmit = async (data) => {
    try {
      const formData = {
        user: {
          userName: data.userName,
          email: data.email,
        },
        city: {
          cityId: data.cityId,
          cityName: cities.find((city) => city.cityId === parseInt(data.cityId))?.cityName || "",
        },
        contact: data.contact,
        address: data.address,
        gstNo: data.gstNo,
      };

      const response = await axios.put(
        `http://localhost:8080/api/car-rental-agencies/${agencyId}`,
        formData,
        {
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      setSuccessMessage("Details updated successfully!");
      setErrorMessage("");
    } catch (error) {
      console.error("Error updating agency details", error);
      setErrorMessage("Failed to update details. Please try again.");
    }
  };

  return (
    <div className="container mt-5 d-flex justify-content-center">
      <div className="col-md-6">
        <h2 className="text-center mb-4">Owner Dashboard</h2>
        <form
          onSubmit={handleSubmit(onSubmit)}
          className="p-4 shadow-lg rounded bg-light"
        >
          <div className="mb-3">
            <label htmlFor="userName" className="form-label">
              Name
            </label>
            <input
              type="text"
              className="form-control"
              id="userName"
              {...register("userName", {
                required: "Name is required",
                minLength: { value: 2, message: "Name must have at least 2 characters" },
              })}
            />
            {errors.userName && <p className="text-danger">{errors.userName.message}</p>}
          </div>

          <div className="mb-3">
            <label htmlFor="email" className="form-label">
              Email
            </label>
            <input
              type="email"
              className="form-control"
              id="email"
              {...register("email", {
                required: "Email is required",
                pattern: {
                  value: /^[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,}$/,
                  message: "Invalid email format",
                },
              })}
              disabled
            />
            {errors.email && <p className="text-danger">{errors.email.message}</p>}
          </div>

          <div className="mb-3">
            <label htmlFor="cityId" className="form-label">
              City
            </label>
            <select
              className="form-control"
              id="cityId"
              {...register("cityId", { required: "City selection is required" })}
            >
              <option value="">Select City</option>
              {cities.map((city) => (
                <option key={city.cityId} value={city.cityId}>
                  {city.cityName}
                </option>
              ))}
            </select>
            {errors.cityId && <p className="text-danger">{errors.cityId.message}</p>}
          </div>

          <div className="mb-3">
            <label htmlFor="contact" className="form-label">
              Contact
            </label>
            <input
              type="text"
              className="form-control"
              id="contact"
              {...register("contact", {
                required: "Contact number is required",
                pattern: {
                  value: /^\d{10}$/,
                  message: "Contact number must be 10 digits",
                },
              })}
            />
            {errors.contact && <p className="text-danger">{errors.contact.message}</p>}
          </div>

          <div className="mb-3">
            <label htmlFor="address" className="form-label">
              Address
            </label>
            <input
              type="text"
              className="form-control"
              id="address"
              {...register("address", { required: "Address is required" })}
            />
            {errors.address && <p className="text-danger">{errors.address.message}</p>}
          </div>

          <div className="mb-3">
            <label htmlFor="gstNo" className="form-label">
              GST Number
            </label>
            <input
              type="text"
              className="form-control"
              id="gstNo"
              {...register("gstNo", {
                required: "GST number is required",
                pattern: {
                  value: /^[a-zA-Z0-9]{15}$/,
                  message: "GST number must be 15 characters",
                },
              })}
            />
            {errors.gstNo && <p className="text-danger">{errors.gstNo.message}</p>}
          </div>

          <button
            type="submit"
            className="btn btn-primary w-100"
            style={{ transition: "background-color 0.5s ease" }}
          >
            Update Details
          </button>

          {successMessage && (
            <div className="alert alert-success mt-3" role="alert">
              {successMessage}
            </div>
          )}
          {errorMessage && (
            <div className="alert alert-danger mt-3" role="alert">
              {errorMessage}
            </div>
          )}
        </form>
      </div>
    </div>
  );
};

export default OwnerDashboard;
