import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";

const UpdateProfile = () => {
  const [cities, setCities] = useState([]);
  const [userProfile, setUserProfile] = useState(null); // To store existing user profile data
  const [successMessage, setSuccessMessage] = useState("");
  const navigate = useNavigate();

  const {
    register,
    handleSubmit,
    formState: { errors },
    setValue, // To set form fields with existing values
  } = useForm({ mode: "onChange" });

  useEffect(() => {
    const fetchCities = async () => {
      try {
        const response = await axios.get("http://localhost:8080/cities");
        setCities(response.data);
      } catch (error) {
        console.error("Error fetching cities", error);
      }
    };

    const fetchUserProfile = async () => {
      try {
        const response = await axios.get("http://localhost:8080/api/user-profile");
        setUserProfile(response.data);

        // Set form values with the current user's profile
        setValue("userName", response.data.userName);
        setValue("email", response.data.email);
        setValue("companyName", response.data.companyName);
        setValue("cityId", response.data.city.cityId);
        setValue("contact", response.data.contact);
        setValue("address", response.data.address);
        setValue("gstNo", response.data.gstNo);
      } catch (error) {
        console.error("Error fetching user profile", error);
      }
    };

    fetchCities();
    fetchUserProfile();
  }, [setValue]);

  const onSubmit = async (data) => {
    try {
      const formData = {
        user: {
          userName: data.userName,
          email: data.email,
          password: data.password, // If password is to be updated
        },
        city: {
          cityId: data.cityId,
          cityName: cities.find((city) => city.cityId === parseInt(data.cityId))?.cityName || "",
        },
        companyName: data.companyName,
        address: data.address,
        contact: data.contact,
        gstNo: data.gstNo,
      };

      const response = await axios.put(
        "http://localhost:8080/api/user-profile/update",
        formData
      );
      setSuccessMessage("Profile updated successfully!");
    } catch (error) {
      console.error("Profile update failed", error);
    }
  };

  const handleHomeClick = () => {
    navigate("/owner-dashboard"); // Redirect to the Owner Dashboard's home page
  };

  return (
    <div className="container mt-5 d-flex justify-content-center">
      <div className="col-md-6">
        <h2 className="text-center mb-4">Update Profile</h2>
        <form onSubmit={handleSubmit(onSubmit)} className="p-4 shadow-lg rounded bg-light">
          <div className="mb-3">
            <label htmlFor="userName" className="form-label">
              Name
            </label>
            <input
              type="text"
              className="form-control"
              id="userName"
              {...register("userName", { required: "Name is required", minLength: { value: 2, message: "Name must have at least 2 characters" } })}
            />
            {errors.userName && <p className="text-danger">{errors.userName.message}</p>}
          </div>

          <div className="mb-3">
            <label htmlFor="email" className="form-label">
              Email
            </label>
            <input
              type="email"
              className="form-control"
              id="email"
              {...register("email", {
                required: "Email is required",
                pattern: {
                  value: /^[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,}$/,
                  message: "Invalid email format",
                },
              })}
            />
            {errors.email && <p className="text-danger">{errors.email.message}</p>}
          </div>

          <div className="mb-3">
            <label htmlFor="companyName" className="form-label">
              Company Name
            </label>
            <input
              type="text"
              className="form-control"
              id="companyName"
              {...register("companyName", { required: "Company name is required" })}
            />
            {errors.companyName && <p className="text-danger">{errors.companyName.message}</p>}
          </div>

          <div className="mb-3">
            <label htmlFor="cityId" className="form-label">
              City
            </label>
            <select
              className="form-control"
              id="cityId"
              {...register("cityId", { required: "City selection is required" })}
            >
              <option value="">Select City</option>
              {cities.map((city) => (
                <option key={city.cityId} value={city.cityId}>
                  {city.cityName}
                </option>
              ))}
            </select>
            {errors.cityId && <p className="text-danger">{errors.cityId.message}</p>}
          </div>

          <div className="mb-3">
            <label htmlFor="contact" className="form-label">
              Contact
            </label>
            <input
              type="number"
              className="form-control"
              id="contact"
              {...register("contact", {
                required: "Contact number is required",
                pattern: {
                  value: /^\d{10}$/,
                  message: "Contact number must be 10 digits",
                },
              })}
            />
            {errors.contact && <p className="text-danger">{errors.contact.message}</p>}
          </div>

          <div className="mb-3">
            <label htmlFor="address" className="form-label">
              Address
            </label>
            <input
              type="text"
              className="form-control"
              id="address"
              {...register("address", { required: "Address is required" })}
            />
            {errors.address && <p className="text-danger">{errors.address.message}</p>}
          </div>

          <div className="mb-3">
            <label htmlFor="gstNo" className="form-label">
              GST Number
            </label>
            <input
              type="text"
              className="form-control"
              id="gstNo"
              {...register("gstNo", {
                required: "GST number is required",
                pattern: {
                  value: /^[0-9]{15}$/,
                  message: "GST number must be 15 digits",
                },
              })}
            />
            {errors.gstNo && <p className="text-danger">{errors.gstNo.message}</p>}
          </div>

          <button
            type="submit"
            className="btn btn-primary w-100"
            style={{ transition: "background-color 0.5s ease" }}
          >
            Update
          </button>

          {successMessage && (
            <div className="alert alert-success mt-3" role="alert">
              {successMessage}
            </div>
          )}
        </form>

        <button onClick={handleHomeClick} className="btn btn-secondary mt-3 w-100">
          Home
        </button>
      </div>
    </div>
  );
};

export default UpdateProfile;
